var arraytemperatura = [];

// los arrays usados en la represenstación gráfica deben contener objetos JSON con dos campos: {x , y}
// en x debemos poner la hora en formato "timestamp". Si tenemos un objeto fecha se puede convertir usando el metodo getTime()
// en y debemos poner el valor de la variable
if (Array.isArray(msg.payload) && msg.payload.length > 0) {
    for (var i = 0; i < msg.payload.length; i++) {
        var timeString = msg.payload[i].tiempo;
        var dateTimeParts = timeString.split(" "); // Separar fecha y hora
        var dateParts = dateTimeParts[0].split("-").map(Number);
        var timeParts = dateTimeParts[1].split(":").map(Number);

        var timestamp = new Date(
            dateParts[0], // Año
            dateParts[1] - 1, // Mes (se resta 1 ya que en JavaScript los meses van de 0 a 11)
            dateParts[2], // Día
            timeParts[0], // Horas
            timeParts[1], // Minutos
            timeParts[2] // Segundos
        ).getTime();

        arraytemperatura.push({
            x: timestamp,
            y: msg.payload[i].temperatura
        });
    }
}


msg.payload = [{
    "series": ["temperatura"],
    "data": [arraytemperatura],
    "labels": [""]
}];


return msg;

